CREATE OR REPLACE FUNCTION public.prevent_duplicate_shift()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
declare
  existing_open_shift uuid;
  current_user_id uuid;
  is_admin boolean := false;
begin
  -- Safely get current user ID (may be null in SQL Editor)
  begin
    select nullif(current_setting('request.jwt.claim.sub', true), '')::uuid into current_user_id;
  exception when others then
    current_user_id := null;
  end;

  -- Verify admin role, if possible
  if current_user_id is not null then
    select public.has_role(current_user_id, 'admin') into is_admin;
  end if;

  --  Apply duplicate-shift check only for non-admins
  if not is_admin then
    select id into existing_open_shift
    from public.shift_logs
    where employee_id = new.employee_id
      and shift_end is null
    limit 1;

    if existing_open_shift is not null then
      raise exception
        ' You already have an active shift (ID: %). Please end it before starting a new one.',
        existing_open_shift;
    end if;
  end if;

  return new;
end;
$function$

