CREATE OR REPLACE FUNCTION public.sync_task_progress()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  total_subitems INTEGER;
  completed_subitems INTEGER;
  new_progress NUMERIC;
  new_status TEXT;
  v_task_id uuid;
BEGIN
  -- Use a variable name that doesn't conflict
  v_task_id := COALESCE(NEW.task_id, OLD.task_id);

  -- Count all subitems for this task
  SELECT COUNT(*), COUNT(*) FILTER (WHERE completed)
  INTO total_subitems, completed_subitems
  FROM public.task_subitems s
  WHERE s.task_id = v_task_id;

  -- Compute new progress percentage
  IF total_subitems > 0 THEN
    new_progress := ROUND((completed_subitems::NUMERIC / total_subitems) * 100, 1);
  ELSE
    new_progress := 0;
  END IF;

  -- Determine new status
  IF new_progress = 100 THEN
    new_status := 'Completed';
  ELSIF new_progress > 0 THEN
    new_status := 'In Progress';
  ELSE
    new_status := 'Pending';
  END IF;

  -- Update parent task
  UPDATE public.tasks
  SET
    progress = new_progress,
    status = new_status,
    updated_at = now()
  WHERE id = v_task_id;

  RETURN NULL;
END;
$function$

CREATE OR REPLACE FUNCTION extensions.grant_pg_graphql_access()
 RETURNS event_trigger
 LANGUAGE plpgsql
AS $function$
DECLARE
    func_is_graphql_resolve bool;
BEGIN
    func_is_graphql_resolve = (
        SELECT n.proname = 'resolve'
        FROM pg_event_trigger_ddl_commands() AS ev
        LEFT JOIN pg_catalog.pg_proc AS n
        ON ev.objid = n.oid
    );

    IF func_is_graphql_resolve
    THEN
        -- Update public wrapper to pass all arguments through to the pg_graphql resolve func
        DROP FUNCTION IF EXISTS graphql_public.graphql;
        create or replace function graphql_public.graphql(
            "operationName" text default null,
            query text default null,
            variables jsonb default null,
            extensions jsonb default null
        )
            returns jsonb
            language sql
        as $$
            select graphql.resolve(
                query := query,
                variables := coalesce(variables, '{}'),
                "operationName" := "operationName",
                extensions := extensions
            );
        $$;

        -- This hook executes when `graphql.resolve` is created. That is not necessarily the last
        -- function in the extension so we need to grant permissions on existing entities AND
        -- update default permissions to any others that are created after `graphql.resolve`
        grant usage on schema graphql to postgres, anon, authenticated, service_role;
        grant select on all tables in schema graphql to postgres, anon, authenticated, service_role;
        grant execute on all functions in schema graphql to postgres, anon, authenticated, service_role;
        grant all on all sequences in schema graphql to postgres, anon, authenticated, service_role;
        alter default privileges in schema graphql grant all on tables to postgres, anon, authenticated, service_role;
        alter default privileges in schema graphql grant all on functions to postgres, anon, authenticated, service_role;
        alter default privileges in schema graphql grant all on sequences to postgres, anon, authenticated, service_role;

        -- Allow postgres role to allow granting usage on graphql and graphql_public schemas to custom roles
        grant usage on schema graphql_public to postgres with grant option;
        grant usage on schema graphql to postgres with grant option;
    END IF;

END;
$function$

